<?php	require_once dirname(__FILE__) . '/lib/WideImage.php';		$path1 = $_GET['image1'];	$path2 = $_GET['image2'];		$width = $_GET['width'];	$height = $_GET['height'];		$img1 = WideImage::load($path1);	$img2 = WideImage::load($path2);			$img1 = $img1->resize($width, $height, 'outside')->crop('center', 'center', $width, $height);	$img2 = $img2->resize($width, $height, 'outside')->crop('center', 'center', $width, $height);		$img1 = $img1->resizeCanvas('200%', '100%', 0, 0);	$img1->merge($img2, 'right', 'top', 100)->output('jpg');	?>